import { sqliteTable, text, integer, real } from "drizzle-orm/sqlite-core"
import { relations } from "drizzle-orm"

// ============ ENUMS (as text with validation) ============
export const difficultyEnum = ['EASY', 'MEDIUM', 'HARD'] as const
export type Difficulty = typeof difficultyEnum[number]

export const bookmarkTypeEnum = ['LESSON', 'NOTE', 'MCQ'] as const
export type BookmarkType = typeof bookmarkTypeEnum[number]

// ============ EXAMS ============
export const exams = sqliteTable('exams', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  name: text('name').notNull(),
  description: text('description'),
  year: integer('year'),
  iconUrl: text('icon_url'),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const examsRelations = relations(exams, ({ many }) => ({
  subjects: many(subjects),
}))

// ============ SUBJECTS ============
export const subjects = sqliteTable('subjects', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  examId: text('exam_id').notNull().references(() => exams.id, { onDelete: 'cascade' }),
  name: text('name').notNull(),
  orderIndex: integer('order_index').default(0),
  iconUrl: text('icon_url'),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const subjectsRelations = relations(subjects, ({ one, many }) => ({
  exam: one(exams, {
    fields: [subjects.examId],
    references: [exams.id],
  }),
  chapters: many(chapters),
}))

// ============ CHAPTERS ============
export const chapters = sqliteTable('chapters', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  subjectId: text('subject_id').notNull().references(() => subjects.id, { onDelete: 'cascade' }),
  title: text('title').notNull(),
  description: text('description'),
  orderIndex: integer('order_index').default(0),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const chaptersRelations = relations(chapters, ({ one, many }) => ({
  subject: one(subjects, {
    fields: [chapters.subjectId],
    references: [subjects.id],
  }),
  lessons: many(lessons),
  mcqs: many(mcqs),
}))

// ============ LESSONS ============
export const lessons = sqliteTable('lessons', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  chapterId: text('chapter_id').notNull().references(() => chapters.id, { onDelete: 'cascade' }),
  title: text('title').notNull(),
  content: text('content').notNull(), // Markdown content
  durationMinutes: integer('duration_minutes'),
  difficulty: text('difficulty').$type<Difficulty>().default('MEDIUM'),
  orderIndex: integer('order_index').default(0),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const lessonsRelations = relations(lessons, ({ one, many }) => ({
  chapter: one(chapters, {
    fields: [lessons.chapterId],
    references: [chapters.id],
  }),
  notes: many(notes),
  progress: many(userLessonProgress),
}))

// ============ NOTES ============
export const notes = sqliteTable('notes', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  lessonId: text('lesson_id').notNull().references(() => lessons.id, { onDelete: 'cascade' }),
  title: text('title').notNull(),
  content: text('content').notNull(),
  isHighlighted: integer('is_highlighted', { mode: 'boolean' }).default(false),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const notesRelations = relations(notes, ({ one }) => ({
  lesson: one(lessons, {
    fields: [notes.lessonId],
    references: [lessons.id],
  }),
}))

// ============ MCQS ============
export const mcqs = sqliteTable('mcqs', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  chapterId: text('chapter_id').notNull().references(() => chapters.id, { onDelete: 'cascade' }),
  question: text('question').notNull(),
  optionA: text('option_a').notNull(),
  optionB: text('option_b').notNull(),
  optionC: text('option_c').notNull(),
  optionD: text('option_d').notNull(),
  correctOption: text('correct_option').notNull(), // 'A', 'B', 'C', 'D'
  explanation: text('explanation'),
  difficulty: text('difficulty').$type<Difficulty>().default('MEDIUM'),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const mcqsRelations = relations(mcqs, ({ one, many }) => ({
  chapter: one(chapters, {
    fields: [mcqs.chapterId],
    references: [chapters.id],
  }),
  attempts: many(mcqAttempts),
}))

// ============ USERS ============
export const users = sqliteTable('users', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  name: text('name').notNull(),
  email: text('email').notNull().unique(),
  passwordHash: text('password_hash'),
  avatarUrl: text('avatar_url'),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const usersRelations = relations(users, ({ many }) => ({
  lessonProgress: many(userLessonProgress),
  mcqAttempts: many(mcqAttempts),
  bookmarks: many(bookmarks),
}))

// ============ USER LESSON PROGRESS ============
export const userLessonProgress = sqliteTable('user_lesson_progress', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  userId: text('user_id').notNull().references(() => users.id, { onDelete: 'cascade' }),
  lessonId: text('lesson_id').notNull().references(() => lessons.id, { onDelete: 'cascade' }),
  progressPercent: integer('progress_percent').default(0),
  completedAt: integer('completed_at', { mode: 'timestamp' }),
})

export const userLessonProgressRelations = relations(userLessonProgress, ({ one }) => ({
  user: one(users, {
    fields: [userLessonProgress.userId],
    references: [users.id],
  }),
  lesson: one(lessons, {
    fields: [userLessonProgress.lessonId],
    references: [lessons.id],
  }),
}))

// ============ MCQ ATTEMPTS ============
export const mcqAttempts = sqliteTable('mcq_attempts', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  userId: text('user_id').notNull().references(() => users.id, { onDelete: 'cascade' }),
  mcqId: text('mcq_id').notNull().references(() => mcqs.id, { onDelete: 'cascade' }),
  selectedOption: text('selected_option').notNull(),
  isCorrect: integer('is_correct', { mode: 'boolean' }).notNull(),
  attemptedAt: integer('attempted_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const mcqAttemptsRelations = relations(mcqAttempts, ({ one }) => ({
  user: one(users, {
    fields: [mcqAttempts.userId],
    references: [users.id],
  }),
  mcq: one(mcqs, {
    fields: [mcqAttempts.mcqId],
    references: [mcqs.id],
  }),
}))

// ============ BOOKMARKS ============
export const bookmarks = sqliteTable('bookmarks', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  userId: text('user_id').notNull().references(() => users.id, { onDelete: 'cascade' }),
  type: text('type').$type<BookmarkType>().notNull(),
  referenceId: text('reference_id').notNull(),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const bookmarksRelations = relations(bookmarks, ({ one }) => ({
  user: one(users, {
    fields: [bookmarks.userId],
    references: [users.id],
  }),
}))

// ============ MOCK TESTS (Optional) ============
export const mockTests = sqliteTable('mock_tests', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  examId: text('exam_id').notNull().references(() => exams.id, { onDelete: 'cascade' }),
  title: text('title').notNull(),
  description: text('description'),
  durationMinutes: integer('duration_minutes').default(60),
  totalMarks: integer('total_marks').default(100),
  isActive: integer('is_active', { mode: 'boolean' }).default(true),
  createdAt: integer('created_at', { mode: 'timestamp' }).$defaultFn(() => new Date()),
})

export const mockTestQuestions = sqliteTable('mock_test_questions', {
  id: text('id').primaryKey().$defaultFn(() => crypto.randomUUID()),
  mockTestId: text('mock_test_id').notNull().references(() => mockTests.id, { onDelete: 'cascade' }),
  mcqId: text('mcq_id').notNull().references(() => mcqs.id, { onDelete: 'cascade' }),
  orderIndex: integer('order_index').default(0),
  marks: integer('marks').default(1),
})

import { Hono } from 'hono'
import { eq } from 'drizzle-orm'
import { db } from '../database'
import * as schema from '../database/schema'
import { z } from 'zod'

const admin = new Hono()

// ============ VALIDATION SCHEMAS ============

const examSchema = z.object({
  name: z.string().min(1).max(100),
  description: z.string().max(500).optional(),
  year: z.number().min(2020).max(2030).optional(),
  iconUrl: z.string().url().optional(),
})

const subjectSchema = z.object({
  examId: z.string().uuid(),
  name: z.string().min(1).max(100),
  orderIndex: z.number().default(0),
  iconUrl: z.string().url().optional(),
})

const chapterSchema = z.object({
  subjectId: z.string().uuid(),
  title: z.string().min(1).max(200),
  description: z.string().max(500).optional(),
  orderIndex: z.number().default(0),
})

const lessonSchema = z.object({
  chapterId: z.string().uuid(),
  title: z.string().min(1).max(200),
  content: z.string().min(1),
  durationMinutes: z.number().min(1).max(120).optional(),
  difficulty: z.enum(['EASY', 'MEDIUM', 'HARD']).default('MEDIUM'),
  orderIndex: z.number().default(0),
})

const noteSchema = z.object({
  lessonId: z.string().uuid(),
  title: z.string().min(1).max(200),
  content: z.string().min(1).max(5000),
  isHighlighted: z.boolean().default(false),
})

const mcqSchema = z.object({
  chapterId: z.string().uuid(),
  question: z.string().min(1).max(2000),
  optionA: z.string().min(1).max(500),
  optionB: z.string().min(1).max(500),
  optionC: z.string().min(1).max(500),
  optionD: z.string().min(1).max(500),
  correctOption: z.enum(['A', 'B', 'C', 'D']),
  explanation: z.string().max(2000).optional(),
  difficulty: z.enum(['EASY', 'MEDIUM', 'HARD']).default('MEDIUM'),
})

// Bulk import schema for entire chapter
const bulkChapterImportSchema = z.object({
  examName: z.string(),
  subjectName: z.string(),
  chapter: z.object({
    title: z.string(),
    description: z.string().optional(),
    orderIndex: z.number().default(0),
  }),
  lessons: z.array(z.object({
    title: z.string(),
    content: z.string(),
    durationMinutes: z.number().optional(),
    difficulty: z.enum(['EASY', 'MEDIUM', 'HARD']).default('MEDIUM'),
    orderIndex: z.number().default(0),
    notes: z.array(z.object({
      title: z.string(),
      content: z.string(),
      isHighlighted: z.boolean().default(false),
    })).default([]),
  })).default([]),
  mcqs: z.array(z.object({
    question: z.string(),
    optionA: z.string(),
    optionB: z.string(),
    optionC: z.string(),
    optionD: z.string(),
    correctOption: z.enum(['A', 'B', 'C', 'D']),
    explanation: z.string().optional(),
    difficulty: z.enum(['EASY', 'MEDIUM', 'HARD']).default('MEDIUM'),
  })).default([]),
})

// ============ EXAM ROUTES ============

admin.get('/exams', async (c) => {
  const database = db(c.env.DB)
  const allExams = await database.select().from(schema.exams)
  return c.json({ success: true, data: allExams })
})

admin.post('/exams', async (c) => {
  const body = await c.req.json()
  const parsed = examSchema.safeParse(body)
  if (!parsed.success) {
    return c.json({ success: false, error: parsed.error.errors }, 400)
  }

  const database = db(c.env.DB)
  const [exam] = await database.insert(schema.exams).values(parsed.data).returning()
  return c.json({ success: true, data: exam }, 201)
})

admin.put('/exams/:id', async (c) => {
  const id = c.req.param('id')
  const body = await c.req.json()
  const parsed = examSchema.partial().safeParse(body)
  if (!parsed.success) {
    return c.json({ success: false, error: parsed.error.errors }, 400)
  }

  const database = db(c.env.DB)
  const [exam] = await database.update(schema.exams)
    .set(parsed.data)
    .where(eq(schema.exams.id, id))
    .returning()

  if (!exam) return c.json({ success: false, error: 'Exam not found' }, 404)
  return c.json({ success: true, data: exam })
})

admin.delete('/exams/:id', async (c) => {
  const id = c.req.param('id')
  const database = db(c.env.DB)
  await database.delete(schema.exams).where(eq(schema.exams.id, id))
  return c.json({ success: true, message: 'Exam deleted' })
})

// ============ SUBJECT ROUTES ============

admin.get('/subjects', async (c) => {
  const examId = c.req.query('examId')
  const database = db(c.env.DB)

  let query = database.select().from(schema.subjects)
  if (examId) {
    query = query.where(eq(schema.subjects.examId, examId)) as typeof query
  }

  const allSubjects = await query
  return c.json({ success: true, data: allSubjects })
})

admin.post('/subjects', async (c) => {
  const body = await c.req.json()
