admin.delete('/subjects/:id', async (c) => {
  const id = c.req.param('id')
  const database = db(c.env.DB)
  await database.delete(schema.subjects).where(eq(schema.subjects.id, id))
  return c.json({ success: true, message: 'Subject deleted' })
