cd /home/user/railway-prep && cat > seed_batch2.sql << 'EOSQL'
-- Batch 2: More Arithmetic MCQs

INSERT INTO mcqs (id, chapter_id, question, option_a, option_b, option_c, option_d, correct_option, explanation, difficulty) VALUES
('mcq-arith-b2-1', 'ch-ntpc-percentage', 'When the price of a radio was reduced by 20%, its sale increased by 80%. What was the net effect on revenue?', '44% increase', '44% decrease', '66% increase', '75% increase', 'A', 'Net effect = -20 + 80 + (-20 times 80)/100 = 60 - 16 = 44% increase', 'MEDIUM');

INSERT INTO mcqs (id, chapter_id, question, option_a, option_b, option_c, option_d, correct_option, explanation, difficulty) VALUES
('mcq-arith-b2-2', 'ch-ntpc-percentage', 'How much water must be added to 48 ml of alcohol to make a solution that contains 25% alcohol?', '24 ml', '72 ml', '144 ml', '196 ml', 'C', 'Let water added = x ml. Then (x+48) times 25/100 = 48, solving x = 144 ml', 'MEDIUM');

INSERT INTO mcqs (id, chapter_id, question, option_a, option_b, option_c, option_d, correct_option, explanation, difficulty) VALUES
('mcq-arith-b2-3', 'ch-ntpc-percentage', 'Ravi salary is 150% of Amit salary. Amit salary is 80% of Ram salary. What is the ratio of Ram salary to Ravi salary?', '1 to 2', '2 to 3', '5 to 6', '6 to 5', 'C', 'Let Ram = 100, Amit = 80, Ravi = 150% of 80 = 120. Ram:Ravi = 100:120 = 5:6', 'MEDIUM');
EOSQL

npx wrangler d1 execute DB --local --file=./seed_batch2.sql 2>&1

